import { animation, trigger, group, transition, animate, style, query, animateChild } from '@angular/animations';

export const transitionAnimation = animation ([
  style({
    height: '{{height}}',
    opacity: '{{opacity}}',
    backgroundColor: '{{backgroundColor}}'
  }),
  animate('{{time}}')
]);
