import { Component } from '@angular/core';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.scss']
})

export class MenuComponent {
  items: string[] = ['Super Loto', 'Super Mega', 'Super Quina','Super Quadra','Sobre'];

  trackByFn(index: number, item: string) {
    return index; 
  }


}


<link
rel="stylesheet"
href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.2/css/all.min.css"
/>

<nav class="nav">
  <ul class="ul">
   <i class="fa-solid fa-dollar-sign" style="color: #fff077;"></i>
   <li class="li"><a routerLink="/" routerLinkActive="active" ariaCurrentWhenActive="page">Super Loto</a></li>
   <li class="li"><a routerLink="/super-mega" routerLinkActive="active" ariaCurrentWhenActive="page">Super Mega</a></li>
   <li class="li"><a routerLink="/super-corner" routerLinkActive="active" ariaCurrentWhenActive="page">Super Quina</a></li>
   <li class="li"><a routerLink="/super-court" routerLinkActive="active" ariaCurrentWhenActive="page">Super Quadra</a></li>
   <li class="li"><a routerLink="/" routerLinkActive="active" ariaCurrentWhenActive="page">Sobre</a></li>
   <i class="fa-brands fa-facebook" style="color: #ffffff;"></i>
   <i class="fa-brands fa-instagram" style="color: #ffffff;"></i>
   <i class="fa-brands fa-youtube" style="color: #ffffff;"></i>
   <i class="fa-brands fa-tiktok" style="color: #ffffff;"></i>
 </ul>
</nav>
