import { Component} from '@angular/core';


@Component({
  selector: 'app-menu-modal',
  templateUrl: './menu-modal.component.html',
  styleUrls: ['./menu-modal.component.scss'],
})
export class MenuModalComponent  {
  viewModal: boolean = false;

  onToggleModal() {
    this.viewModal = !this.viewModal;
    setTimeout(() => {
        const modalElement = document.querySelector('.modal') as HTMLElement;
        if (this.viewModal) {
            modalElement.classList.remove('hidden');
        } else {
            modalElement.classList.add('hidden');
        }
    }, 100); // Ajuste este valor conforme necessário
}



}

















<link
  rel="stylesheet"
  href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.2/css/all.min.css"
/>

<div class="nav" [ngClass]="{'hidden': !viewModal}">
  <div
    class="icon-fa-solid-fa-bars"
    (click)="onToggleModal()"
    [ngClass]="{ showModal: viewModal, 'hidden':!viewModal }"
  >
    <i class="fa-solid fa-bars" style="color: #ffffff"></i>
  </div>


  <div
    (click)="onToggleModal()"
    class="category-modal"
    [ngClass]="{ showModal: viewModal }"
  >
    <span class="onToggleModal">
      <i class="fa-solid fa-xmark" style="color: #0a0901;"></i>

    </span>
  </div>
</div>
